package javaBasic;

public class JavaOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		int b=10;
		System.out.println("The sum of "+a+" and "+b+ " = "+(a+b));
		System.out.println("The Minsu of "+a+" and "+b+ " = "+(a-b));
		System.out.println("The Multiple of "+a+" and "+b+ " = "+(a*b));
		System.out.println("The Division of "+a+" and "+b+ " = "+(a/b));
		System.out.println("The Mod of "+a+" and "+b+ " = "+(a%b));
		
		System.out.println(a>b);
		System.out.println(a<b);
		System.out.println(a==b);
		System.out.println(a!=b);
	}

}
